pan class="modifier">public</span>    <span class="type" style="color:#EAB766">void</span>     <span class="methodname" style="color:#CC7832">{@link EvWatcher::invoke}</span>     ( <span class="methodparam">     <span class="type" style="color:#EAB766">int</span>      <span class="parameter" style="color:#2EACF9">$revents</span>    </span>    )</div> <div class="methodsynopsis dc-description" style="border:1px gray;padding-left:5px;background:#232525">    <span class="modifier">public</span>    <span class="type" style="color:#EAB766">bool</span>     <span class="methodname" style="color:#CC7832">{@link EvWatcher::keepalive}</span>     ([ <span class="methodparam">     <span class="type" style="color:#EAB766">bool</span>      <span class="parameter" style="color:#2EACF9">$value</span>    </span>   ] )</div> <div class="methodsynopsis dc-description" style="border:1px gray;padding-left:5px;background:#232525">    <span class="modifier">public</span>    <span class="type" style="color:#EAB766">void</span>     <span class="methodname" style="color:#CC7832">{@link EvWatcher::setCallback}</span>     ( <span class="methodparam">     <span class="type" style="color:#EAB766"><a href="http://php.net/manual/zh/language.types.callable.php" class="type callable" style="color:#EAB766">callable</a></span>      <span class="parameter" style="color:#2EACF9">$callback</span>    </span>    )</div> <div class="methodsynopsis dc-description" style="border:1px gray;padding-left:5px;background:#232525">    <span class="modifier">public</span>    <span class="type" style="color:#EAB766">void</span>     <span class="methodname" style="color:#CC7832">{@link EvWatcher::start}</span>     ( <span class="methodparam">void</span>    )</div> <div class="methodsynopsis dc-description" style="border:1px gray;padding-left:5px;background:#232525">    <span class="modifier">public</span>    <span class="type" style="color:#EAB766">void</span>     <span class="methodname" style="color:#CC7832">{@link E