    public function __construct(callable $callback, $data = null, $priority = 0) {}

/**
*<div id="evprepare.createstopped" class="refentry">  <div class="refnamediv">   <h1 class="refname">EvPrepare::createStopped</h1>   <p class="verinfo">(PECL ev &gt;= 0.2.0)</p><p class="refpurpose"><span class="refname">EvPrepare::createStopped</span> &mdash; <span class="dc-title">Creates a stopped instance of EvPrepare watcher</span></p>   </div>  <br></br><div style="BORDER-TOP: gray 1px dashed; OVERFLOW: hidden; HEIGHT: 1px"></div><div class="refsect1 description" id="refsect1-evprepare.createstopped-description">   <h3 class="title">说明</h3>   <div class="methodsynopsis dc-description" style="border:1px gray;padding-left:5px;background:#232525">    <span class="modifier">final</span>    <span class="modifier">public</span>    <span class="modifier">static</span>    <span class="type" style="color:#EAB766"><a href="http://php.net/manual/zh/class.evprepare.php" class="type EvPrepare" style="color:#EAB766">EvPrepare</a></span>     <span class="methodname" style="color:#CC7832"><strong>EvPrepare::createStopped</strong></span>     ( <span class="methodparam">     <span class="type" style="color:#EAB766"><a href="http://php.net/manual/zh/language.types.callable.php" class="type callable" style="color:#EAB766">callable</a></span>      <span class="parameter" style="color:#2EACF9">$callback</span>    </span>    [, <span class="methodparam">     <span class="type" style="color:#EAB766"><a href="http://php.net/manual/zh/language.pseudo-types.php#language.types.mixed" class="type mixed" style="color:#EAB766">mixed</a></span>      <span class="parameter" style="color:#2EACF9">$data</span>     <span class="initializer"> = <strong><span>NULL</span></strong></span>    </span>    [, <span class="methodparam">     <span class="type" style="color:#EAB766">int</span>      <span class="parameter" style="color:#2EACF9">$priority</span>     <span class="initializer"> = 0</span>    </span>   ]] )</div>    <p class="para rdfs-comment">    Creates a stopped instance of EvPrepare watcher. Unlike    <span class="methodname" style="color:#CC7832">{@link EvPrepare::__construct()}</span>    , this method doesn&#039; start the watcher automatically.   </p>  </div>   <br></br><div style="BORDER-TOP: gray 1px dashed; OVERFLOW: hidden; HEIGHT: 1px"></div><div class="refsect1 parameters" id="refsect1-evprepare.createstopped-parameters">   <h3 class="title">参数</h3>   <dl>          <dt>       <span class="parameter" style="color:#2EACF9">callback</span>     </dt>      <dd>       <span>       See       <a href="http://php.net/manual/zh/ev.watcher-callbacks.php" class="link">Watcher callbacks</a>       .      </span>     </dd>              <dt>       <span class="parameter" style="color:#2EACF9">data</span>     </dt>      <dd>       <p class="para">       Custom data associated with the watcher.      </p>     </dd>              <dt>       <span class="parameter" style="color:#2EACF9">priority</span>     </dt>      <dd>       <p class="para">       <a href="http://php.net/manual/zh/class.ev.php#ev.constants.watcher-pri" class="link">Watcher priority</a>      </p>     </dd>        </dl>   </div>   <br></br><div style="BORDER-TOP: gray 1px dashed; OVERFLOW: hidden; HEIGHT: 1px"></div><div class="refsect1 returnvalues" id="refsect1-evprepare.createstopped-returnvalues">   <h3 class="title">返回值</h3>   <p class="para">    Return EvPrepare object on success.   </p>  </div>   <br></br><div style="BORDER-TOP: gray 1px dashed; OVERFLOW: hidden; HEIGHT: 1px"></div><div class="refsect1 seealso" id="refsect1-evprepare.createstopped-seealso">   <h3 class="title">参见</h3>   <ul class="simplelist">    <li class="member">     <span class="methodname" style="color:#CC7832">{@link EvPrepare::__construct()} - Constructs EvPrepare watcher object</span>    </li>    <li class="member">     <span class="methodname" style="color:#CC7832">{@link EvWatcher::start()} - Starts the watcher</span>    </li>   </ul>  </div>  <div class="up"><a href="http://php.net/manual/zh/class.evprepare.php">EvPrepare</a></div></div>
