/span><span style="color: #9876AA">is_array</span><span style="color: #007700">(</span><span style="color: #9876AA">$address_array</span><span style="color: #007700">)&nbsp;||&nbsp;</span><span style="color: #9876AA">count</span><span style="color: #007700">(</span><span style="color: #9876AA">$address_array</span><span style="color: #007700">)&nbsp;&lt;&nbsp;</span><span style="color: #9876AA">1</span><span style="color: #007700">)&nbsp;{<br />&nbsp;&nbsp;&nbsp;&nbsp;die(</span><span style="color: #DD0000">"something&nbsp;is&nbsp;wrong\n"</span><span style="color: #007700">);<br />}<br /><br />foreach&nbsp;(</span><span style="color: #9876AA">$address_array&nbsp;</span><span style="color: #007700">as&nbsp;</span><span style="color: #9876AA">$id&nbsp;</span><span style="color: #007700">=&gt;&nbsp;</span><span style="color: #9876AA">$val</span><span style="color: #007700">)&nbsp;{<br />&nbsp;&nbsp;&nbsp;&nbsp;echo&nbsp;</span><span style="color: #DD0000">"#&nbsp;</span><span style="color: #9876AA">$id</span><span style="color: #DD0000">\n"</span><span style="color: #007700">;<br />&nbsp;&nbsp;&nbsp;&nbsp;echo&nbsp;</span><span style="color: #DD0000">"&nbsp;&nbsp;mailbox&nbsp;:&nbsp;"&nbsp;</span><span style="color: #007700">.&nbsp;</span><span style="color: #9876AA">$val</span><span style="color: #007700">-&gt;</span><span style="color: #9876AA">mailbox&nbsp;</span><span style="color: #007700">.&nbsp;</span><span style="color: #DD0000">"\n"</span><span style="color: #007700">;<br />&nbsp;&nbsp;&nbsp;&nbsp;echo&nbsp;</span><span style="color: #DD0000">"&nbsp;&nbsp;host&nbsp;&nbsp;&nbsp;&nbsp;:&nbsp;"&nbsp;</span><span style="color: #007700">.&nbsp;</span><span style="color: #9876AA">$val</span><span style="color: #007700">-&gt;</span><span style="color: #9876AA">host&nbsp;</span><span style="color: #007700">.&nbsp;</span><span style="color: #DD0000">"\n"</span><span style="color: #007700">;<br />&nbsp;&nbsp;&nbsp;&nbsp;echo&nbsp;</span><span style="color: #DD0000">"&n