*/
define('OAUTH_HTTP_METHOD_GET', 'GET');
/**
*       为 OAuth 请求使用 <em class="emphasis">POST</em> 方法。      
*/
define('OAUTH_HTTP_METHOD_POST', 'POST');
/**
*       为 OAuth 请求使用 <em class="emphasis">PUT</em> 方法。      
*/
define('OAUTH_HTTP_METHOD_PUT', 'PUT');
/**
*       为 OAuth 请求使用 <em class="emphasis">HEAD</em> 方法。      
*/
define('OAUTH_HTTP_METHOD_HEAD', 'HEAD');
/**
*       为 OAuth 请求使用 <em class="emphasis">DELETE</em> 方法。      
*/
define('OAUTH_HTTP_METHOD_DELETE', 'DELETE');

/**
*       使用 <span class="methodname">{@link OAuth::setRequestEngine()}</span> 来设置引擎为 <a href="http://php.net/manual/zh/book.stream.php" class="link">PHP 流</a>，与用 <strong><code>OAUTH_REQENGINE_CURL</code></strong> 的 <a href="http://php.net/manual/zh/book.curl.php" class="link">Curl</a> 截然相反。      
