define('OAUTH_CONSUMER_KEY_UNKNOWN', 16);
/**
*       使用者密钥遭拒绝。      
*/
define('OAUTH_CONSUMER_KEY_REFUSED', 32);
/**
*        <em class="emphasis">oauth_signature</em> 无效，因为和服务提供者的签名计算不匹配。      
*/
define('OAUTH_INVALID_SIGNATURE', 64);
/**
*       <em class="emphasis">oauth_token</em> 已经被消费。此令牌不能再被使用，因为在上一次请求中已经使用过。      
*/
define('OAUTH_TOKEN_USED', 128);
/**
*       <em class="emphasis">oauth_token</em> 已经过期。      
*/
define('OAUTH_TOKEN_EXPIRED', 256);
/**
*       <em class="emphasis">oauth_token</em> 被服务提供者拒绝。原因未知，也许是因为令牌从未发布、已经消费、过期、或服务提供者忘记了。      
*/
define('OAUTH_TOKEN_REJECTED', 1024);
/**
*       <em class="emphasis">oauth_verifier</em> 不正确。      
*/
define('OAUTH_VERIFIER_INVALID', 2048);
