<?php
/**
*<div id="class.rararchive" class="reference">   <h1 class="title">The RarArchive class</h1>     <div class="partintro"><p class="verinfo">(PECL rar &gt;= 2.0.0)</p>     <div class="section" id="rararchive.intro">    <h2 class="title">简介</h2>    <p class="para">     This class represents a RAR archive, which may be formed by several volumes (parts) and which contains     a number of RAR entries (i.e., files, directories and other special objects such as symbolic links).    </p>    <p class="para">     Objects of this class can be traversed, yielding the entries stored in the respective RAR archive.     Those entries can also be obtained through <span class="methodname">{@link RarArchive::getEntry()}</span> and     <span class="methodname">{@link RarArchive::getEntries()}</span>.    </p>   </div>     <div class="section" id="rararchive.synopsis">    <h2 class="title">类摘要</h2>      <div class="classsynopsis">     <div class="ooclass"></div>       <div class="classsynopsisinfo">      <span class="ooclass">       <span class="modifier">final</span>       <strong class="classname">RarArchive</strong>      </span>            <span class="oointerface">implements        <span class="interfacename"><strong class="interfacename">Traversable</strong></span>      </span>      {</div>           <div class="classsynopsisinfo classsynopsisinfo_comment">// 方法 </div>     <div class="methodsynopsis dc-description">    <span class="modifier">public</span> <span class="type">bool</span> <span class="methodname"><a href="http://php.net/manual/zh/rararchive.close.php" class="methodname">close</a></span>     ( <span class="methodparam">void</span>     )</div> <div class="methodsynopsis dc-description">    <span class="modifier">public</span> <span class="type">string</span> <span class="methodname"><a href="http://php.net/manual/zh/rararchive.getcomment.php" class="methodname">getComment</a></span>     ( <span class="methodparam">void</span>     )</div> <div class="methodsynopsis dc-description">    <span class="modifier">public</span> <span class="type">array</span> <span class="methodname"><a href="http://php.net/manual/zh/rararchive.getentries.php" class="methodname">getEntries</a></span>     ( <span class="methodparam">void</span>     )</div> <div class="methodsynopsis dc-description">    <span class="modifier">public</span> <span class="type">RarEntry</span> <span class="methodname"><a href="http://php.net/manual/zh/rararchive.getentry.php" class="methodname">getEntry</a></span>     ( <span class="methodparam"><span class="type">string</span> <code class="parameter">$entryname</code></span>     )</div> <div class="methodsynopsis dc-description">    <span class="modifier">public</span> <span class="type">bool</span> <span class="methodname"><a href="http://php.net/manual/zh/rararchive.isbroken.php" class="methodname">isBroken</a></span>     ( <span class="methodparam">void</span>     )</div> <div class="methodsynopsis dc-description">    <span class="modifier">public</span> <span class="type">bool</span> <span class="methodname"><a href="http://php.net/manual/zh/rararchive.issolid.php" class="methodname">isSolid</a></span>     ( <span class="methodparam">void</span>     )</div> <div class="methodsynopsis dc-description">    <span class="modifier">public</span> <span class="modifier">static</span> <span class="type">RarArchive</span> <span class="methodname"><a href="http://php.net/manual/zh/rararchive.open.php" class="methodname">open</a></span>     ( <span class="methodparam"><span class="type">string</span> <code class="parameter">$filename</code></span>    [, <span class="methodparam"><span class="type">string</span> <code class="parameter">$password</code><span class="initializer"> = NULL</span></span>    [, <span class="methodparam"><span class="type"><a href="http://php.net/manual/zh/language.types.callable.php" class="type callable">callable</a></span> <code class="parameter">$volume_callback</code><span class="initializer"> = NULL</span></span>    ]] )</div> <div class="methodsynopsis dc-description">     <span class="modifier">public</span> <span class="type">bool</span> <span class="methodname"><a href="http://php.net/manual/zh/rararchive.setallowbroken.php" class="methodname">setAllowBroken</a></span>      ( <span class="methodparam"><span class="type">bool</span> <code class="parameter">$allow_broken</code></span>     )</div> <div class="methodsynopsis dc-description">    <span class="modifier">public</span> <span class="type">string</span> <span class="methodname"><a href="http://php.net/manual/zh/rararchive.tostring.php" class="methodname">__toString</a></span>     ( <span class="methodparam">void</span>    )</div>     }</div>     </div>   </div>                                                                    <h2>Table of Contents</h2><ul class="chunklist chunklist_reference"><li>{@link RarArchive::close} — Close RAR archive and free all resources</li><li>{@link RarArchive::getComment} — Get comment text from the RAR archive</li><li>{@link RarArchive::getEntries} — Get full list of entries from the RAR archive</li><li>{@link RarArchive::getEntry} — Get entry object from the RAR archive</li><li>{@link RarArchive::isBroken} — Test whether an archive is broken (incomplete)</li><li>{@link RarArchive::isSolid} — Check whether the RAR archive is solid</li><li>{@link RarArchive::open} — Open RAR archive</li><li>{@link RarArchive::setAllowBroken} — Whether opening broken archives is allowed</li><li>{@link RarArchive::__toString} — Get text representation</li></ul> <div class="up"><a href="http://php.net/manual/zh/book.rar.php">Rar</a></div></div>
*/
final class RarArchive implements Traversable
{
/**
*<div id="rararchive.open" class="refentry">  <div class="refnamediv">   <h1 class="refname">RarArchive::open</h1>   <h1 class="refname">rar_open</h1>   <p class="verinfo">(PECL rar &gt;= 2.0.0)</p><p class="refpurpose"><span class="refname">RarArchive::open</span> -- <span class="refname">rar_open</span> &mdash; <span class="dc-title">Open RAR archive</span></p>   </div>   <div class="refsect1 description" id="refsect1-rararchive.open-description">   <h3 class="title">说明</h3>   <p class="para">面向对象风格 (method):</p>    <div class="methodsynopsis dc-description">    <span class="modifier">public</span> <span class="modifier">static</span> <span class="type"><a href="http://php.net/manual/zh/class.rararchive.php" class="type RarArchive">RarArchive</a></span> <span class="methodname"><strong>RarArchive::open</strong></span>     ( <span class="methodparam"><span class="type">string</span> <code class="parameter">$filename</code></span>    [, <span class="methodparam"><span class="type">string</span> <code class="parameter">$password</code><span class="initializer"> = NULL</span></span>    [, <span class="methodparam"><span class="type"><a href="http://php.net/manual/zh/language.types.callable.php" class="type callable">callable</a></span> <code class="parameter">$volume_callback</code><span class="initializer"> = NULL</span></span>    ]] )</div>    <p class="para rdfs-comment">过程化风格:</p>   <div class="methodsynopsis dc-description">    <span class="type"><a href="http://php.net/manual/zh/class.rararchive.php" class="type RarArchive">RarArchive</a></span> <span class="methodname"><strong>rar_open</strong></span>     ( <span class="methodparam"><span class="type">string</span> <code class="parameter">$filename</code></span>    [, <span class="methodparam"><span class="type">string</span> <code class="parameter">$password</code><span class="initializer"> = NULL</span></span>    [, <span class="methodparam"><span class="type"><a href="http://php.net/manual/zh/language.types.callable.php" class="type callable">callable</a></span> <code class="parameter">$volume_callback</code><span class="initializer"> = NULL</span></span>   ]] )</div>    <p class="para rdfs-comment">    Open specified RAR archive and return <span class="type"><a href="http://php.net/manual/zh/class.rararchive.php" class="type RarArchive">RarArchive</a></span> instance representing it.   </p>   <blockquote class="note"><p><strong class="note">Note</strong>:     <p class="para">     If opening a multi-volume archive, the path of the first volume should be passed as the first parameter.     Otherwise, not all files will be shown. This is by design.    </p>   </p></blockquote>  </div>    <div class="refsect1 parameters" id="refsect1-rararchive.open-parameters">   <h3 class="title">参数</h3>   <p class="para">    <dl>            <dt> <code class="parameter">filename</code></dt>       <dd>        <p class="para">        Path to the Rar archive.       </p>      </dd>                 <dt> <code class="parameter">password</code></dt>       <dd>        <p class="para">        A plain password, if needed to decrypt the headers. It will also be used by default        if encrypted files are found. Note that the files may have        different passwords in respect to the headers and among them.       </p>      </dd>                 <dt> <code class="parameter">volume_callback</code></dt>       <dd>        <p class="para">        A function that receives one parameter – the path of the volume        that was not found – and returns a string with the correct path        for such volume or <strong><code>NULL</code></strong> if such volume does        not exist or is not known. The programmer should ensure the        passed function doesn&#039;t cause loops as this function is called        repeatedly if the path returned in a previous call did not        correspond to the needed volume. Specifying this parameter omits        the notice that would otherwise be emitted whenever a volume is        not found; an implementation that only returns <strong><code>NULL</code></strong>        can therefore be used to merely omit such notices.       </p>      </dd>          </dl>    </p>   <div class="warning"><strong class="warning">Warning</strong>    <p class="para">     Prior to version 2.0.0, this function would not handle relative     paths correctly. Use <span class="function">{@link realpath()}</span> as a workaround.    </p>   </div>  </div>    <div class="refsect1 returnvalues" id="refsect1-rararchive.open-returnvalues">   <h3 class="title">返回值</h3>   <p class="para">    Returns the requested <span class="type"><a href="http://php.net/manual/zh/class.rararchive.php" class="type RarArchive">RarArchive</a></span> instance  或者在失败时返回 <strong><code>FALSE</code></strong>.   </p>  </div>    <div class="refsect1 changelog" id="refsect1-rararchive.open-changelog">   <h3 class="title">更新日志</h3>   <p class="para">    <table class="doctable informaltable">           <thead>       <tr>        <th>版本</th>        <th>说明</th>       </tr>       </thead>       <tbody class="tbody">       <tr>        <td>3.0.0</td>        <td>         <code class="parameter">volume_callback</code> was added.        </td>       </tr>       </tbody>         </table>    </p>  </div>    <div class="refsect1 examples" id="refsect1-rararchive.open-examples">   <h3 class="title">范例</h3>   <p class="para">    <div class="example" id="example-806">     <p><strong>Example #1 面向对象风格</strong></p>     <div class="example-contents"> <div class="phpcode"><code><span style="color: #000000"> <span style="color: #9876AA">&lt;?php<br />$rar_arch&nbsp;</span><span style="color: #007700">=&nbsp;</span><span style="color: #9876AA">RarArchive</span><span style="color: #007700">::</span><span style="color: #9876AA">open</span><span style="color: #007700">(</span><span style="color: #DD0000">'encrypted_headers.rar'</span><span style="color: #007700">,&nbsp;</span><span style="color: #DD0000">'samplepassword'</span><span style="color: #007700">);<br />if&nbsp;(</span><span style="color: #9876AA">$rar_arch&nbsp;</span><span style="color: #007700">===&nbsp;</span><span style="color: #9876AA">FALSE</span><span style="color: #007700">)<br />&nbsp;&nbsp;&nbsp;&nbsp;die(</span><span style="color: #DD0000">"Failed&nbsp;opening&nbsp;file"</span><span style="color: #007700">);<br />&nbsp;&nbsp;&nbsp;&nbsp;<br /></span><span style="color: #9876AA">$entries&nbsp;</span><span style="color: #007700">=&nbsp;</span><span style="color: #9876AA">$rar_arch</span><span style="color: #007700">-&gt;</span><span style="color: #9876AA">getEntries</span><span style="color: #007700">();<br />if&nbsp;(</span><span style="color: #9876AA">$entries&nbsp;</span><span style="color: #007700">===&nbsp;</span><span style="color: #9876AA">FALSE</span><span style="color: #007700">)<br />&nbsp;&nbsp;&nbsp;&nbsp;die(</span><span style="color: #DD0000">"Failed&nbsp;fetching&nbsp;entries"</span><span style="color: #007700">);<br /><br />echo&nbsp;</span><span style="color: #DD0000">"Found&nbsp;"&nbsp;</span><span style="color: #007700">.&nbsp;</span><span style="color: #9876AA">count</span><span style="color: #007700">(</span><span style="color: #9876AA">$entries</span><span style="color: #007700">)&nbsp;.&nbsp;</span><span style="color: #DD0000">"&nbsp;files.\n"</span><span style="color: #007700">;<br /><br />if&nbsp;(empty(</span><span style="color: #9876AA">$entries</span><span style="color: #007700">))<br />&nbsp;&nbsp;&nbsp;&nbsp;die(</span><span style="color: #DD0000">"No&nbsp;valid&nbsp;entries&nbsp;found."</span><span style="color: #007700">);<br />&nbsp;&nbsp;&nbsp;&nbsp;<br /></span><span style="color: #9876AA">$stream&nbsp;</span><span style="color: #007700">=&nbsp;</span><span style="color: #9876AA">reset</span><span style="color: #007700">(</span><span style="color: #9876AA">$entries</span><span style="color: #007700">)-&gt;</span><span style="color: #9876AA">getStream</span><span style="color: #007700">();<br />if&nbsp;(</span><span style="color: #9876AA">$stream&nbsp;</span><span style="color: #007700">===&nbsp;</span><span style="color: #9876AA">FALSE</span><span style="color: #007700">)<br />&nbsp;&nbsp;&nbsp;&nbsp;die(</span><span style="color: #DD0000">"Failed&nbsp;opening&nbsp;first&nbsp;file"</span><span style="color: #007700">);<br /><br /></span><span style="color: #9876AA">$rar_arch</span><span style="color: #007700">-&gt;</span><span style="color: #9876AA">close</span><span style="color: #007700">();<br /><br />echo&nbsp;</span><span style="color: #DD0000">"Content&nbsp;of&nbsp;first&nbsp;one&nbsp;follows:\n"</span><span style="color: #007700">;<br />echo&nbsp;</span><span style="color: #9876AA">stream_get_contents</span><span style="color: #007700">(</span><span style="color: #9876AA">$stream</span><span style="color: #007700">);<br /><br /></span><span style="color: #9876AA">fclose</span><span style="color: #007700">(</span><span style="color: #9876AA">$stream</span><span style="color: #007700">);<br /></span><span style="color: #9876AA">?&gt;</span> </span> </code></div>      </div>       <div class="example-contents"><p>以上例程的输出类似于：</p></div>      <div class="example-contents screen"> <div class="cdata"><pre> Found 2 files. Content of first one follows: Encrypted file 1 contents. </pre></div>     </div>    </div>   </p>   <p class="para">    <div class="example" id="example-807">     <p><strong>Example #2 过程化风格</strong></p>     <div class="example-contents"> <div class="phpcode"><code><span style="color: #000000"> <span style="color: #9876AA">&lt;?php<br />$rar_arch&nbsp;</span><span style="color: #007700">=&nbsp;</span><span style="color: #9876AA">rar_open</span><span style="color: #007700">(</span><span style="color: #DD0000">'encrypted_headers.rar'</span><span style="color: #007700">,&nbsp;</span><span style="color: #DD0000">'samplepassword'</span><span style="color: #007700">);<br />if&nbsp;(</span><span style="color: #9876AA">$rar_arch&nbsp;</span><span style="color: #007700">===&nbsp;</span><span style="color: #9876AA">FALSE</span><span style="color: #007700">)<br />&nbsp;&nbsp;&nbsp;&nbsp;die(</span><span style="color: #DD0000">"Failed&nbsp;opening&nbsp;file"</span><span style="color: #007700">);<br />&nbsp;&nbsp;&nbsp;&nbsp;<br /></span><span style="color: #9876AA">$entries&nbsp;</span><span style="color: #007700">=&nbsp;</span><span style="color: #9876AA">rar_list</span><span style="color: #007700">(</span><span style="color: #9876AA">$rar_arch</span><span style="color: #007700">);<br />if&nbsp;(</span><span style="color: #9876AA">$entries&nbsp;</span><span style="color: #007700">===&nbsp;</span><span style="color: #9876AA">FALSE</span><span style="color: #007700">)<br />&nbsp;&nbsp;&nbsp;&nbsp;die(</span><span style="color: #DD0000">"Failed&nbsp;fetching&nbsp;entries"</span><span style="color: #007700">);<br /><br />echo&nbsp;</span><span style="color: #DD0000">"Found&nbsp;"&nbsp;</span><span style="color: #007700">.&nbsp;</span><span style="color: #9876AA">count</span><span style="color: #007700">(</span><span style="color: #9876AA">$entries</span><span style="color: #007700">)&nbsp;.&nbsp;</span><span style="color: #DD0000">"&nbsp;files.\n"</span><span style="color: #007700">;<br /><br />if&nbsp;(empty(</span><span style="color: #9876AA">$entries</span><span style="color: #007700">))<br />&nbsp;&nbsp;&nbsp;&nbsp;die(</span><span style="color: #DD0000">"No&nbsp;valid&nbsp;entries&nbsp;found."</span><span style="color: #007700">);<br />&nbsp;&nbsp;&nbsp;&nbsp;<br /></span><span style="color: #9876AA">$stream&nbsp;</span><span style="color: #007700">=&nbsp;</span><span style="color: #9876AA">reset</span><span style="color: #007700">(</span><span style="color: #9876AA">$entries</span><span style="color: #007700">)-&gt;</span><span style="color: #9876AA">getStream</span><span style="color: #007700">();<br />if&nbsp;(</span><span style="color: #9876AA">$stream&nbsp;</span><span style="color: #007700">===&nbsp;</span><span style="color: #9876AA">FALSE</span><span style="color: #007700">)<br />&nbsp;&nbsp;&nbsp;&nbsp;die(</span><span style="color: #DD0000">"Failed&nbsp;opening&nbsp;first&nbsp;file"</span><span style="color: #007700">);<br /><br /></span><span style="color: #9876AA">rar_close</span><span style="color: #007700">(</span><span style="color: #9876AA">$rar_arch</span><span style="color: #007700">);<br /><br />echo&nbsp;</span><span style="color: #DD0000">"Content&nbsp;of&nbsp;first&nbsp;one&nbsp;follows:\n"</span><span style="color: #007700">;<br />echo&nbsp;</span><span style="color: #9876AA">stream_get_contents</span><span style="color: #007700">(</span><span style="color: #9876AA">$stream</span><span style="color: #007700">);<br /><br /></span><span style="color: #9876AA">fclose</span><span style="color: #007700">(</span><span style="color: #9876AA">$stream</span><span style="color: #007700">);<br /></span><span style="color: #9876AA">?&gt;</span> </span> </code></div>      </div>     </div>   </p>   <p class="para">    <div class="example" id="example-808">     <p><strong>Example #3 Volume Callback</strong></p>     <div class="example-contents"> <div class="phpcode"><code><span style="color: #000000"> <span style="color: #9876AA">&lt;?php<br /></span><span style="color: #FF8000">//&nbsp;In&nbsp;this&nbsp;example,&nbsp;there's&nbsp;a&nbsp;volume&nbsp;named&nbsp;multi_broken.part1.rar<br />&nbsp;*&nbsp;whose&nbsp;next&nbsp;volume&nbsp;is&nbsp;named&nbsp;multi.part2.rar&nbsp;<br /></span><span style="color: #007700">function&nbsp;</span><span style="color: #9876AA">resolve</span><span style="color: #007700">(</span><span style="color: #9876AA">$vol</span><span style="color: #007700">)&nbsp;{<br />&nbsp;&nbsp;&nbsp;&nbsp;if&nbsp;(</span><span style="color: #9876AA">preg_match</span><span style="color: #007700">(</span><span style="color: #DD0000">'/_broken/'</span><span style="color: #007700">,&nbsp;</span><span style="color: #9876AA">$vol</span><span style="color: #007700">))<br />&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;return&nbsp;</span><span style="color: #9876AA">str_replace</span><span style="color: #007700">(</span><span style="color: #DD0000">'_broken'</span><span style="color: #007700">,&nbsp;</span><span style="color: #DD0000">''</span><span style="color: #007700">,&nbsp;</span><span style="color: #9876AA">$vol</span><span style="color: #007700">);<br />&nbsp;&nbsp;&nbsp;&nbsp;else<br />&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;return&nbsp;</span><span style="color: #9876AA">null</span><span style="color: #007700">;<br />}<br /></span><span style="color: #9876AA">$rar_file1&nbsp;</span><span style="color: #007700">=&nbsp;</span><span style="color: #9876AA">rar_open</span><span style="color: #007700">(</span><span style="color: #9876AA">dirname</span><span style="color: #007700">(</span><span style="color: #9876AA">__FILE__</span><span style="color: #007700">).</span><span style="color: #DD0000">'/multi_broken.part1.rar'</span><span style="color: #007700">,&nbsp;</span><span style="color: #9876AA">null</span><span style="color: #007700">,&nbsp;</span><span style="color: #DD0000">'resolve'</span><span style="color: #007700">);<br /></span><span style="color: #9876AA">$entry&nbsp;</span><span style="color: #007700">=&nbsp;</span><span style="color: #9876AA">$rar_file1</span><span style="color: #007700">-&gt;</span><span style="color: #9876AA">getEntry</span><span style="color: #007700">(</span><span style="color: #DD0000">'file2.txt'</span><span style="color: #007700">);<br /></span><span style="color: #9876AA">$entry</span><span style="color: #007700">-&gt;</span><span style="color: #9876AA">extract</span><span style="color: #007700">(</span><span style="color: #9876AA">null</span><span style="color: #007700">,&nbsp;</span><span style="color: #9876AA">dirname</span><span style="color: #007700">(</span><span style="color: #9876AA">__FILE__</span><span style="color: #007700">)&nbsp;.&nbsp;</span><span style="color: #DD0000">"/temp_file2.txt"</span><span style="color: #007700">);<br /></span><span style="color: #9876AA">?&gt;</span> </span> </code></div>      </div>     </div>   </p>  </div>     <div class="refsect1 seealso" id="refsect1-rararchive.open-seealso">   <h3 class="title">参见</h3>   <p class="para">    <ul class="simplelist">     <li class="member"><a href="http://php.net/manual/zh/wrappers.rar.php" class="link"><em>rar://</em> wrapper</a></li>    </ul>   </p>  </div>    <div class="up"><a href="http://php.net/manual/zh/class.rararchive.php">RarArchive</a></div></div>
     * @return RarArchive the requested RarArchive instance or FALSE on failure.*/
    public static function open($filename, $password = null, callable $volume_callback = null)
